Task<Result<int, string>> asyncResult = GetValueAsync();
var validated = await asyncResult.BindAsync(x => 
    x > 0 ? Result<int, string>.Success(x) 
          : Result<int, string>.Failure("Must be positive"));

var result = Result<int, string>.Success(42);
var output = await result.MatchAsync(
    async value => await FormatSuccessAsync(value),
    error => $"Error: {error}"
);

var result = Result<int, string>.Success(42);
var saved = await result.MatchAsync(
    async value => await SaveSuccessAsync(value),
    async error => await LogErrorAsync(error)
);

var result = Result<int, string>.Success(5);
var validated = result.Bind(x => 
    x > 0 ? Result<int, string>.Success(x) 
          : Result<int, string>.Failure("Must be positive"));

var result = Result<int, string>.Success(42);
var message = result.Match(
    value => $"Success: {value}",
    error => $"Error: {error}"
); // "Success: 42"

// Success case
var success = Result<int, string>.Success(42);

// Failure case
var failure = Result<int, string>.Failure("Something went wrong");

var result = ResultExtensions.Try(
    () => int.Parse("42"),
    ex => $"Parse failed: {ex.Message}"
); // Success(42)

var failed = ResultExtensions.Try(
    () => int.Parse("invalid"),
    ex => $"Parse failed: {ex.Message}"
); // Failure("Parse failed: ...")

var result = await ResultExtensions.TryAsync(
    async () => await httpClient.GetStringAsync("https://api.example.com"),
    ex => $"HTTP request failed: {ex.Message}"
);

var result = Result<int, string>.Success(5);
var validated = result.Ensure(
    x => x > 0,
    "Value must be positive"
); // Success(5)

var invalid = result.Ensure(
    x => x > 10,
    "Value must be greater than 10"
); // Failure("Value must be greater than 10")

var result = Result<int, string>.Success(42)
    .Tap(x => Console.WriteLine($"Value: {x}"))
    .Map(x => x * 2);
// Prints "Value: 42" and returns Success(84)

var result = await Result<User, string>.Success(user)
    .TapAsync(async u => await LogUserActivityAsync(u))
    .MapAsync(u => u.Id);

var result = await GetUserAsync()
    .TapAsync(async u => await LogUserActivityAsync(u))
    .MapAsync(u => u.Id);

var result = OpenFile("data.txt")
    .Using(stream => ReadData(stream));
// stream is automatically disposed

var result = await OpenFileAsync("data.txt")
    .UsingAsync(async stream => await ReadDataAsync(stream));
// stream is automatically disposed
